import { Component, OnInit } from '@angular/core';

interface KpiMetric {
  label: string;
  value: string;
  growth: string;
  icon: string;
  color: 'blue' | 'green' | 'orange' | 'purple' | 'emerald';
}

@Component({
  selector: 'app-kpi-cards',
  templateUrl: './kpi-cards.component.html',
  styleUrls: ['./kpi-cards.component.scss']
})
export class KpiCardsComponent implements OnInit {
  kpiMetrics: KpiMetric[] = [
    {
      label: 'Total Campaigns',
      value: '24',
      growth: '+12%',
      icon: 'pi-megaphone',
      color: 'blue'
    },
    {
      label: 'Open Rate',
      value: '68.5%',
      growth: '+5.2%',
      icon: 'pi-envelope-open',
      color: 'green'
    },
    {
      label: 'Click Rate',
      value: '24.8%',
      growth: '-2.1%',
      icon: 'pi-cursor',
      color: 'orange'
    },
    {
      label: 'Conversion Rate',
      value: '8.4%',
      growth: '+3.7%',
      icon: 'pi-chart-line',
      color: 'purple'
    },
    {
      label: 'Total Spend',
      value: '$12.4K',
      growth: '+8.9%',
      icon: 'pi-dollar',
      color: 'emerald'
    }
  ];

  constructor() { }

  ngOnInit(): void { }

  getTrendIcon(growth: string): string {
    return growth.startsWith('+') ? 'pi-trending-up' : 'pi-trending-down';
  }

  getTrendColor(growth: string): string {
    return growth.startsWith('+') ? 'text-success' : 'text-danger';
  }
}
